import React from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCheckSquare, faSquare } from "@fortawesome/free-solid-svg-icons";
import Add from './AddStudents.module.css';
const AddStudents = ({
  students,
  users,
  handleAddStudent,
  handleChangeFilter,
  handleSearch,
  addStudent,
  filterByName,
  search
}) => {
  return (
    <div className={Add.search_form}>
      <div className={Add.input_title}>
        Добави ученици:
      </div>
      <div className={Add.search_container}>
          <input
              className={Add.input_content}
              placeholder="Търси ученик"
              name="addStudent"
              type="text"
              onChange={handleSearch}
              value={addStudent}
          />
        <div>
            <div className={Add.checkBox}>
              {filterByName ? (
                  <FontAwesomeIcon

                      icon={faSquare}
                      onClick={()=>handleChangeFilter()}
                  />
              ) : (
                  <FontAwesomeIcon
                      icon={faCheckSquare}
                      onClick={()=>handleChangeFilter()}
                  />
              )}
              Търси по ЕГН
            </div>
            <div >
              {filterByName ? (
                  <FontAwesomeIcon

                      icon={faCheckSquare}
                      onClick={()=>handleChangeFilter()}
                  />
              ) : (
                  <FontAwesomeIcon

                      icon={faSquare}
                      onClick={()=>handleChangeFilter()}
                  />
              )}
              Търси по Име,Презиме,Фамилия
            </div>
        </div>
      </div>
    </div>
  );
};

AddStudents.propTypes = {};

export default AddStudents;
