import React from "react";
import axios from "axios";
import "./components/ProductCreate.css";
import slugify from "slugify";

class ProductCreateContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      users: [],
      search:[],
      addStudent:"",
      title: "",
      years: "",
      teacher: "",
      quantity: 0,
      students: [],
      categories: [],
      subcategories: [],
      isLoading: true,
      errors: {},
      error: "",
      searching:false
    };
  }

  componentDidMount() {
    this.handleGetCategory();
    this.handleGetUsers();
  }
  handleInput = event => {
    this.setState({
      ...this.state,
      [event.target.name]: event.target.value
    });
  };

  handleChangeParent = parent => {
    this.setState({ category_title: parent.label });
  };
  handleGetUsers = async () => {
    await axios
      .get("http://localhost:5000/users/")
      .then(response => {
        this.setState({ users: response.data });
      })
      .catch(function(error) {});
  };
  handleGetCategory = async () => {
    await axios
      .get("http://localhost:5000/category/")
      .then(response => {
        response.data.map(category => {
          this.handleBuildCategory(category, response.data);
        });
        this.state.subcategories.map(sub => {
          if (!sub.parent_id) {
            this.state.categories.push(sub);
          }
        });
        this.setState({ isLoading: false });
      })
      .catch(function(error) {});
  };
  handleBuildCategory = (category, categories) => {
    let result = categories.filter(sub => sub.parent_id === category._id);

    if (result.length > 0) {
      category.subcategory = result;
      this.state.subcategories.push(category);
    } else {
      if (!category.parent_id) {
        this.state.subcategories.push(category);
      }
    }
  };
  validateForm = data => {
    const error = {};

    if (data.title.length < 3) {
      error.title = "Името е твърде късо.";
    }
    if (data.price < 1) {
      error.price = "Липсва цена.";
    }
    if (data.category_title.length < 3) {
      error.category = "Изберете категория.";
    }
    if (data.weight < 1) {
      error.weight = "Добавете тегло.";
    }
    if (data.sku < 1) {
      error.sku = "Добавете SKU.";
    }
    if (data.quantity < 1) {
      error.quantity = "Добавете бройка.";
    }
    if (data.imageLink < 1) {
      error.imageLink = "Добавете снимка.";
    }
    return error;
  };



  handleSearch=(event)=>{
    this.setState({
      ...this.state,
      [event.target.name]: event.target.value
    });
    let students=[]
    this.state.users.map(user=>{
      if(user.username.includes(event.target.value)){
        students.push(user)
      }
    })
    this.setState({search:students})
  }


clear=()=>{
    this.setState({searching:true})
}
  handleSubmit = () => {
    const { title, years, students, teacher, quantity } = this.state;
    const errors = this.validateForm(this.state);
    const isValid = Object.values(errors).filter(Boolean).length <= 0;
    if (!isValid) {
      this.setState({ errors: errors });
      return;
    }
    this.setState({ request: "" });
    const subject = {
      title: title,
      years: years,
      students: students,
      teacher: teacher,
      quantity: quantity,
      slug: slugify(title, { lower: true, remove: /[():.@><,_{}]/g })
    };
    axios
      .post("http://localhost:5000/products/add", subject)
      .then(res =>
        this.setState({
          title: "",
          years: 0,
          students: [],
          teacher: "",
          quantity: 0
        })
      )
      .catch(error =>
        this.setState({ error: "Името или SKU на пеодукта е вече използвано." })
      );
  };
  render() {
    const { title, discount_price, sku, quantity, errors } = this.state;
    return (
      <div className="create_product">
        <div className="form_row">
          <div className="row">
            {" "}
            <div className="form_body">
              <div>
                <div>Наименование на предмета</div>
                <input
                  className="form_input"
                  name="title"
                  type="text"
                  onChange={this.handleInput}
                  value={title}
                />
                <div style={{ color: "red" }}>
                  {errors.category ? errors.category : ""}
                </div>
              </div>
            </div>
            <div>
              {" "}
              <div>Учител</div>
              <input
                className="form_input"
                name="discount_price"
                type="text"
                onChange={this.handleInput}
                value={discount_price}
              />
              <div style={{ color: "red" }}>
                {errors.discount_price ? errors.discount_price : ""}
              </div>
            </div>
          </div>
        </div>

        <div className="form_row">
          <div className="row">
            <div className="form_body">
              <div>Година на обучение</div>
              <input
                className="form_input"
                name="sku"
                type="text"
                onChange={this.handleInput}
                value={sku}
              />
              <div style={{ color: "red" }}>{errors.sku ? errors.sku : ""}</div>
            </div>
            <div>
              <div>Бройка</div>
              <input
                className="form_input"
                placeholder="Бройка"
                name="quantity"
                type="number"
                onChange={this.handleInput}
                value={quantity}
              />
              <div style={{ color: "red" }}>
                {errors.quantity ? errors.quantity : ""}
              </div>
            </div>
          </div>
          <div>
            <div>Добави ученици</div>
            <input
              className="form_input"
              placeholder="Търси ученик"
              name="addStudent"
              type="text"
              onChange={this.handleSearch}
              value={this.state.addStudent}
            />
            {this.state.search.slice(-10).reverse().map(stu=>(
                <div>
                  {stu.username}
                  <button className="create_button" onClick={this.clear}>Добави</button>
                </div>
            ))}
            <div style={{ color: "red" }}>
              {errors.quantity ? errors.quantity : ""}
            </div>

          </div>
          <div>
            {this.state.students.map(student => (
              <div>
                {student.name} {student.secondName} {student.familyName}
                {"-"}{student.username}{", "}
              </div>
            ))}
          </div>
        </div>
        <button className="create_button" onClick={this.handleSubmit}>
          Създай
        </button>

        <div>{this.state.error}</div>
      </div>
    );
  }
}

ProductCreateContainer.propTypes = {};

export default ProductCreateContainer;
