import React from "react";
import YearCreate from "./components/YearCreate.module.css";
import Year from "./components/year/Year";
import CreateGrade from "./components/createGarde/CreateGrade";
import AddStudents from "./components/addStudents/AddStudents";
import axios from "axios";

class YearCreateContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      yearFrom: null,
      yearTo: null,
      grade: null,
      subGrade: "",
      teacher: "",
      students: [],
      users: [],
      addStudent: "",
      filterByName: false,
      search: []
    };
  }
  componentDidMount() {
    this.handleGetUsers();
  }

  handleInput = event => {
    this.setState({
      ...this.state,
      [event.target.name]: event.target.value
    });
  };
  handleGetUsers = async () => {
    await axios
      .get("http://localhost:5000/users/")
      .then(response => {
        this.setState({ users: response.data });
      })
      .catch(function(error) {});
  };
  handleSearch = event => {
    this.setState({
      ...this.state,
      [event.target.name]: event.target.value
    });
    let students = [];
    if (event.target.value.trim().length === 0) {
      this.setState({ search: [] });
    } else {
      this.state.users.map(user => {
        if (this.state.filterByName) {
          if (
            user.name.includes(event.target.value) ||
            user.secondName.includes(event.target.value) ||
            user.familyName.includes(event.target.value)
          ) {
            students.push(user);
          }
        } else {
          if (user.username.includes(event.target.value.trim())) {
            students.push(user);
          }
        }
      });
      this.setState({ search: students });
    }
    students = [];
  };
  handleChangeFilter = () => {
    if (this.state.filterByName) {
      this.setState({ filterByName: false });
    } else {
      this.setState({ filterByName: true });
    }
  };
  handleAddStudent = student => {
    let stu = [];
    this.state.students.push(student);
    this.state.students.map(student => {
      stu = this.state.users.filter(user => user._id !== student._id);
    });
    this.setState({ search: [], users: stu, addStudent: "" });
  };
  render() {
    const {
      yearFrom,
      yearTo,
      teacher,
      grade,
      subGrade,
      students,
      users,
      addStudent,
      filterByName,
      search
    } = this.state;
    return (
      <div className={YearCreate.year_container}>
        <div>
          <div>Класове</div>
          <div>
            <div className={YearCreate.year_create_title}>
              Създаване на учебна година
            </div>
            <div className={YearCreate.year_create_form}>
              <Year
                yearFrom={yearFrom}
                yearTo={yearTo}
                handleInput={this.handleInput}
              />
              <CreateGrade
                handleInput={this.handleInput}
                teacher={teacher}
                grade={grade}
                subGrade={subGrade}
              />
              <AddStudents
                users={users}
                students={students}
                handleAddStudent={this.handleAddStudent}
                handleChangeFilter={this.handleChangeFilter}
                handleSearch={this.handleSearch}
                addStudent={addStudent}
                filterByName={filterByName}
                search={search}
              />
            </div>
          </div>
        </div>
      </div>
    );
  }
}

YearCreateContainer.propTypes = {};

export default YearCreateContainer;
