import React from "react";
import InitialSchool from "./components/InitialSchool";
import JuniorSchool from "./components/JuniorSchool";
import HighSchool from "./components/HighSchool";
import "./components/Grade.css";
import SubGradeModal from "./components/subGradeModal/SubGradeModal";

class GradeListContainer extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      initialSchool: [
        { id: 1, grade: 1 },
        { id: 2, grade: 2 },
        { id: 3, grade: 3 },
        { id: 4, grade: 4 }
      ],
      juniorSchool: [
        { id: 5, grade: 5 },
        { id: 6, grade: 6 },
        { id: 7, grade: 7 }
      ],
      highSchool: [
        { id: 8, grade: 8 },
        { id: 9, grade: 9 },
        { id: 10, grade: 10 },
        { id: 11, grade: 11 },
        { id: 12, grade: 12 }
      ],
      subGradeModalIsOpen: false
    };
  }
  handleOpenModal = () => {
    const { subGradeModalIsOpen } = this.state;
    if (subGradeModalIsOpen) {
      this.setState({subGradeModalIsOpen: false});
      console.log('close')
    } else {
      this.setState({subGradeModalIsOpen: true});
      console.log('open')
    }
  };

  render() {
    const {
      initialSchool,
      juniorSchool,
      highSchool,
      subGradeModalIsOpen
    } = this.state;
    const { year } = this.props;
    return (
      <div className="grade_container">
        <div className="year_grade_title">
          Учебна {year}/{year + 1} година
        </div>
        <InitialSchool initial={initialSchool} openModal={this.handleOpenModal}/>
        <JuniorSchool junior={juniorSchool} />
        <HighSchool high={highSchool} />
        <SubGradeModal modalIsOpen={subGradeModalIsOpen} />
      </div>
    );
  }
}

GradeListContainer.propTypes = {};

export default GradeListContainer;
