import React from "react";
import PropTypes from "prop-types";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faCheckSquare, faSquare } from "@fortawesome/free-solid-svg-icons";

const AddStudents = ({
  students,
  users,
  handleAddStudent,
  handleChangeFilter,
  handleSearch,
  addStudent,
  filterByName,
  search
}) => {
  return (
    <div className="row">
      <div>
        <div className="text_year">Добави ученици</div>
        <input
          className="form_addStu"
          placeholder="Търси ученик"
          name="addStudent"
          type="text"
          onChange={handleSearch}
          value={addStudent}
        />
      </div>
        <div>
          <div>
          <div className="form_filter">
            {filterByName ? (
              <FontAwesomeIcon
                className="form_filter"
                icon={faSquare}
                onClick={() => handleChangeFilter()}
              />
            ) : (
              <FontAwesomeIcon
                className="form_filter"
                icon={faCheckSquare}
                onClick={() => handleChangeFilter()}
              />
            )}
            Търси по ЕГН
          </div>
          <div className="form_filter">
            {filterByName ? (
              <FontAwesomeIcon
                className="form_filter"
                icon={faCheckSquare}
                onClick={() => handleChangeFilter()}
              />
            ) : (
              <FontAwesomeIcon
                className="form_filter"
                icon={faSquare}
                onClick={() => handleChangeFilter()}
              />
            )}
            Търси по Име,Презиме,Фамилия
          </div>
        </div>
      </div>
      <div className="form_search">
        <table>
          <tbody>
            {search.map(stu => (
              <tr className="search_row">
                <td>{stu.name}</td>
                <td>{stu.secondName}</td>
                <td>{stu.familyName}</td>
                <td>{stu.username}</td>
                <td>
                  <button
                    className="stu_add"
                    onClick={() => handleAddStudent(stu)}
                  >
                    Добави
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

AddStudents.propTypes = {};

export default AddStudents;
